import React from 'react'
import AthenaImage from '../images/DALL·E 2024-12-31 15.02.47 - A majestic depiction of the Greek goddess Athena, radiating with a golden glowing aura. She stands in a powerful stance, clad in a detailed, ornate ar.webp'
import { Parallax, ParallaxLayer } from '@react-spring/parallax'
const Hero = () => {
  return (
    <div className='text-white'>
        <Parallax pages={2}>
            <ParallaxLayer
            offset={0}
                speed = {1}
                factor = {2}
                style={{
                        background
                    }} >

                <img src={AthenaImage} alt='Athena Image' className=''/>
            </ParallaxLayer>
            <ParallaxLayer offset={1}>
                <h2>ATHENA</h2>

            </ParallaxLayer>

        </Parallax>
    </div>
  )
}

export default Hero
