import React, { useState } from "react";
import axios from "axios";

const GradingApp = () => {
    const [gradingResult, setGradingResult] = useState(null);
    const [error, setError] = useState(null);
    const [file, setFile] = useState(null);
    const [condition, setCondition] = useState(""); // Added for AI condition

    const API_URL = "https://f63b-2620-8d-8000-e018-1565-79d8-5e8e-4dd2.ngrok-free.app";

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setGradingResult(null);

        try {
            if (file) {
                const formData = new FormData();
                formData.append("file", file);
                formData.append("condition", condition); // Add condition to the request

                const response = await axios.post(`${API_URL}/grade`, formData, {
                    headers: { "Content-Type": "multipart/form-data" },
                });
                setGradingResult(response.data.grading_result);
            } else {
                setError("Please upload a file.");
            }
        } catch (err) {
            setError(err.response?.data?.error || "Something went wrong.");
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Grading App</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        Upload File:
                        <input
                            type="file"
                            onChange={(e) => setFile(e.target.files[0])}
                            required
                        />
                    </label>
                </div>
                <div>
                    <label>
                        AI Condition:
                        <input
                            type="text"
                            value={condition}
                            onChange={(e) => setCondition(e.target.value)}
                            placeholder="Enter the condition for the AI"
                            required
                        />
                    </label>
                </div>
                <button type="submit">Grade</button>
            </form>

            {gradingResult && (
                <div>
                    <h2>Grading Result</h2>
                    <p>{gradingResult}</p>
                </div>
            )}
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default GradingApp;
