import React from 'react';
import { useSpring, animated } from 'react-spring';

const SlidingBlocks = () => {
  // Animation for the left block
  const leftBlockAnimation = useSpring({
    from: { transform: 'translateX(-100%)' },
    to: { transform: 'translateX(0)' },
    config: { tension: 200, friction: 20 },
  });

  // Animation for the right block
  const rightBlockAnimation = useSpring({
    from: { transform: 'translateX(100%)' },
    to: { transform: 'translateX(0)' },
    config: { tension: 200, friction: 20 },
  });

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: '100vh',
        background: '#0d1117', // Dark background
        color: '#ffffff',
        padding: '0 5%',
      }}
    >
      {/* Left Block */}
      <animated.div
        style={{
          ...leftBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #6a5acd, #8a2be2)', // Gradient for style
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Left Block</h2>
      </animated.div>

      {/* Right Block */}
      <animated.div
        style={{
          ...rightBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #ff4500, #ff6347)', // Gradient for style
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Right Block</h2>
      </animated.div>
    </div>
  );
};

export default SlidingBlocks;
