import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import SlidingBlocks from './SlidingBlocks';

const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={2}>
        {/* Background Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain', // Adjusts to fit the image within the height
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh', // Sets the height to 90% of the viewport height
          }}
        />

        {/* Foreground Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            Hey, I am Athena.
          </h1>
        </ParallaxLayer>

        {/* Additional Content */}
      </Parallax>
      <div>
      <SlidingBlocks/>
      </div>

    </div>
  );
};

export default Hero;
