import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import SlidingBlocks from './SlidingBlocks';

const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={2}>
        {/* Background Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain', // Adjusts to fit the image within the height
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh', // Sets the height to 90% of the viewport height
          }}
        />

        {/* Foreground Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            Hey, I am Athena.
          </h1>
        </ParallaxLayer>

        {/* Additional Content */}
      </Parallax>
      <div
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: '100vh',
        background: '#0d1117', // Dark background
        color: '#ffffff',
        padding: '0 5%',
      }}
    >
      {/* Left Block */}
      <animated.div
        style={{
          ...leftBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #6a5acd, #8a2be2)', // Gradient for style
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Left Block</h2>
      </animated.div>

      {/* Right Block */}
      <animated.div
        style={{
          ...rightBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #ff4500, #ff6347)', // Gradient for style
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Right Block</h2>
      </animated.div>
    </div>

    </div>
  );
};

export default Hero;
