import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import { ReactTyped } from 'react-typed';
import { useInView } from 'react-intersection-observer';

const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={2}>
        {/* Background Layer */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh',
          }}
        />

        {/* Additional Content */}
        <ParallaxLayer
          offset={1}
          speed={0.3}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <TypedSection />
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

const TypedSection = () => {
  const { ref, inView } = useInView({
    threshold: 0.5, // Trigger when 50% of the component is visible
    triggerOnce: true, // Trigger only once
  });

  return (
    <div ref={ref} >
      {inView && (
        <ReactTyped
          strings={[
            "Hi, I'm Athena. I take the stress out of assessing assignments, so you can focus on what truly matters – teaching.",
          ]}
          typeSpeed={40}
        />
      )}
    </div>
  );
};

export default Hero;
