import React, { useState } from "react";

const GradingApp = () => {
    const [question, setQuestion] = useState("");
    const [studentAnswer, setStudentAnswer] = useState("");
    const [gradingResult, setGradingResult] = useState(null);
    const [error, setError] = useState(null);
    const [file, setFile] = useState(null);
    const [inputType, setInputType] = useState("text"); // "text" or "file"

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setGradingResult(null);

        try {
            if (inputType === "file" && file) {
                const formData = new FormData();
                formData.append("file", file);

                const response = await axios.post("http://localhost:5000/grade", formData, {
                    headers: { "Content-Type": "multipart/form-data" },
                });
                setGradingResult(response.data.grading_result);
            } else if (inputType === "text") {
                const response = await axios.post("http://localhost:5000/grade", {
                    question,
                    student_answer: studentAnswer,
                });
                setGradingResult(response.data.grading_result);
            } else {
                setError("Please select a valid input type and provide input.");
            }
        } catch (err) {
            setError(err.response?.data?.error || "Something went wrong.");
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Grading App</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        <input
                            type="radio"
                            value="text"
                            checked={inputType === "text"}
                            onChange={() => setInputType("text")}
                        />
                        Text Input
                    </label>
                    <label style={{ marginLeft: "20px" }}>
                        <input
                            type="radio"
                            value="file"
                            checked={inputType === "file"}
                            onChange={() => setInputType("file")}
                        />
                        File Upload
                    </label>
                </div>

                {inputType === "text" && (
                    <div>
                        <label>
                            Question:
                            <textarea
                                value={question}
                                onChange={(e) => setQuestion(e.target.value)}
                                rows="4"
                                cols="50"
                                required
                            />
                        </label>
                        <label>
                            Student Answer:
                            <textarea
                                value={studentAnswer}
                                onChange={(e) => setStudentAnswer(e.target.value)}
                                rows="4"
                                cols="50"
                                required
                            />
                        </label>
                    </div>
                )}

                {inputType === "file" && (
                    <div>
                        <label>
                            Upload File:
                            <input
                                type="file"
                                onChange={(e) => setFile(e.target.files[0])}
                                required
                            />
                        </label>
                    </div>
                )}

                <button type="submit">Grade</button>
            </form>

            {gradingResult && (
                <div>
                    <h2>Grading Result</h2>
                    <p>{gradingResult}</p>
                </div>
            )}
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default GradingApp;
