import React, { useRef } from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import { useSpring, animated } from 'react-spring';

const Hero = () => {
  const parallaxRef = useRef();

  // Animation hooks for the left and right blocks
  const [leftBlockStyle, leftBlockApi] = useSpring(() => ({
    transform: 'translateX(-100%)',
  }));
  const [rightBlockStyle, rightBlockApi] = useSpring(() => ({
    transform: 'translateX(100%)',
  }));

  const handleScroll = (event) => {
    const progress = event.target.scrollTop / window.innerHeight;

    // Show the blocks when scrolling down to the second section
    if (progress >= 1 && progress < 2) {
      leftBlockApi.start({ transform: 'translateX(0%)' });
      rightBlockApi.start({ transform: 'translateX(0%)' });
    } else {
      // Hide the blocks when scrolling away
      leftBlockApi.start({ transform: 'translateX(-100%)' });
      rightBlockApi.start({ transform: 'translateX(100%)' });
    }
  };

  return (
    <div
      className="text-white"
      onScroll={handleScroll}
      style={{
        height: '200vh', // Total page height to enable scrolling
        overflow: 'scroll',
        position: 'relative',
      }}
    >
      <Parallax ref={parallaxRef} pages={2}>
        {/* Athena Section */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh',
          }}
        />
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            Hey, I am Athena.
          </h1>
        </ParallaxLayer>

        {/* Sliding Blocks Section */}
        <ParallaxLayer offset={1} speed={0.5}>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              height: '100vh',
              padding: '0 5%',
              background: '#1a1a1a',
              color: 'white',
            }}
          >
            {/* Left Block */}
            <animated.div
              style={{
                ...leftBlockStyle,
                width: '45%',
                height: '300px',
                background: 'linear-gradient(45deg, #6a5acd, #8a2be2)',
                borderRadius: '10px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
              }}
            >
              <h2>Explore Wisdom</h2>
            </animated.div>

            {/* Right Block */}
            <animated.div
              style={{
                ...rightBlockStyle,
                width: '45%',
                height: '300px',
                background: 'linear-gradient(45deg, #ff4500, #ff6347)',
                borderRadius: '10px',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
              }}
            >
              <h2>Unlock Strategy</h2>
            </animated.div>
          </div>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default Hero;
