import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import { useSpring, animated } from 'react-spring';

const SlidingBlocks = () => {
  const leftBlockAnimation = useSpring({
    from: { transform: 'translateX(-100%)' },
    to: { transform: 'translateX(0)' },
    config: { tension: 200, friction: 20 },
  });

  const rightBlockAnimation = useSpring({
    from: { transform: 'translateX(100%)' },
    to: { transform: 'translateX(0)' },
    config: { tension: 200, friction: 20 },
  });

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        height: '100vh',
        padding: '0 5%',
        background: '#1a1a1a', // Dark background for contrast
        color: 'white',
      }}
    >
      {/* Left Block */}
      <animated.div
        style={{
          ...leftBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #6a5acd, #8a2be2)',
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Explore Wisdom</h2>
      </animated.div>

      {/* Right Block */}
      <animated.div
        style={{
          ...rightBlockAnimation,
          width: '45%',
          height: '300px',
          background: 'linear-gradient(45deg, #ff4500, #ff6347)',
          borderRadius: '10px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        }}
      >
        <h2>Unlock Strategy</h2>
      </animated.div>
    </div>
  );
};

const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={3}>
        {/* Athena Section */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh',
          }}
        />
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            Hey, I am Athena.
          </h1>
        </ParallaxLayer>

        {/* Sliding Blocks Section */}
        <ParallaxLayer offset={1} speed={0.5}>
          <SlidingBlocks />
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default Hero;
