import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';

const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={2}>
        {/* Background Layer */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain', // Adjusts to fit the image within the height
            backgroundRepeat: 'no-repeat', // Prevents tiling
            backgroundPosition: 'center',
            height: '90vh', // Sets the height to 50% of the viewport height
          }}
        />

        {/* Foreground Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            ATHENA
          </h1>
        </ParallaxLayer>

        {/* Additional Content */}
        <ParallaxLayer
          offset={1}
          speed={0.3}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h2
            style={{
              fontSize: '3rem',
              textShadow: '1px 1px 3px rgba(0, 0, 0, 0.6)',
            }}
          >
            The Goddess of Wisdom and Strategy
          </h2>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default Hero;
