import React from 'react';
import AthenaImage from '../images/PerfectAthenaPhotoUpdated2.png';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import { ReactTyped } from 'react-typed';


const Hero = () => {
  return (
    <div className="text-white">
      <Parallax pages={2}>
        {/* Background Layer */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          factor={2}
          style={{
            backgroundImage: `url(${AthenaImage})`,
            backgroundSize: 'contain', // Adjusts to fit the image within the height
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            height: '90vh', // Sets the height to 90% of the viewport height
          }}
        />

        {/* Foreground Layer */}
        <ParallaxLayer
          offset={0.2}
          speed={1}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h1
            style={{
              fontSize: '5rem',
              textShadow: '2px 2px 5px rgba(0, 0, 0, 0.8)',
              margin: 0,
            }}
          >
            Hey, I am Athena.
          </h1>
        </ParallaxLayer>
       {/* Additional Content */}
       <ParallaxLayer
          offset={1}
          speed={0.3}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
          }}
        >
          <h2
            style={{
              fontSize: '3rem',
              textShadow: '1px 1px 3px rgba(0, 0, 0, 0.6)',
            }}
          >
            <div>
                <Typed strings={"Goddess of Wisdom and Strategy"} typeSpeed={120}/>

            </div>

          </h2>
        </ParallaxLayer>

      </Parallax>
    </div>
  );
};

export default Hero;
